import{_ as e,c as t,a0 as r,o}from"./chunks/framework.CsJcTknV.js";const m=JSON.parse('{"title":"固化","description":"","frontmatter":{"title":"固化","date":"2019-09-05T11:05:24.000Z","tags":["心情"],"categories":"随笔"},"headers":[],"relativePath":"2019/固化.md","filePath":"2019/固化.md"}'),s={name:"2019/固化.md"};function i(n,a,l,d,c,p){return o(),t("div",null,a[0]||(a[0]=[r('<h4 id="丧-如有不适可迅速离开" tabindex="-1">丧（如有不适可迅速离开） <a class="header-anchor" href="#丧-如有不适可迅速离开" aria-label="Permalink to &quot;丧（如有不适可迅速离开）&quot;">​</a></h4><p><em>生活逐渐固化。每天同样的时间、同样的地点去坐车，来到工作单位期待有不一样的工作内容，但是连工作内容也如一年前的内容差不多，同样是修修补补，可能表面有些许、但是本质又是一模一样。到了晚上下班、去同样的地点等车、同样的地方上车、又去到同样的地点转车。ps：如果要说大数据可怕的地方在哪里，它可以随随便便就知道你每个时间点最有可能在干嘛，因为你每天所干的事情，都是固化的。</em></p><h4 id="可悲的成年人" tabindex="-1">可悲的成年人 <a class="header-anchor" href="#可悲的成年人" aria-label="Permalink to &quot;可悲的成年人&quot;">​</a></h4><p>怎么样衡量你已经成为了一个可悲的成年人。等你变成了一个期待着时间快速流逝，好让你快点进入假期的时候，然后进入假期也同样的无所事事，却希望时间过的慢一点，好让工作迟一点的到来，然后这两个状态无限次的来回切换。在这两个状态中间看不到任何希望，这就是可悲的成年人。</p><h4 id="改变" tabindex="-1">改变 <a class="header-anchor" href="#改变" aria-label="Permalink to &quot;改变&quot;">​</a></h4><p>你说要改变？要从自身开始？谈何容易。你的生活轨迹早就已经固化了，思想也早就腐坏，支撑着自己仅有的那点想法的，可能就是在微博热搜上面吐槽两句，看看世间还有什么值得关注的事情，就算这些事情离你非常遥远。你说不能这样，要去尝试。好吧，我去。我尝试去跑步、跳舞、游泳、健身、剪辑视频、拍照...我可能没告诉过你我尝试过多少，又有多少无用功。可能经过了那些事情我已经有了变化、只是我自己不知道罢了，但是我现在真的已经接受了生活的固化，暂时没有去改变它的毅力。只是想每天下班回家躺着，然后起来又去上班坐着，就是那么简单。</p>',6)]))}const f=e(s,[["render",i]]);export{m as __pageData,f as default};
